	private static int findIndex(String[] tab, String value){
		for (int index = 0; index < tab.length; index++) {
			if (tab[index].equals(value)) return index;
		}
		
		return -1;
	}
	
	private static void coding(){
		String[] splitText = text.split(" ");
		String codeText = "";
		
		for (String word : splitText) {
			String[] splitWord = word.split("");
			
			for (String character : splitWord) {
				codeText += binAlphabet[findIndex(normalAlphabet, character)];
				
				if (findIndex(splitWord, character) < splitWord.length - 1) codeText += ".";
			}
			
			if (findIndex(splitText, word) < splitText.length - 1) codeText += "-";
		}
		
		System.out.println(codeText);
		
	}
	
	private static void decode(){
		String[] splitCode = code.split("-");
		String descriptCode = "";
		
		for (String word : splitCode) {
			String[] splitWord = word.split("\\.");

			for (String  character: splitWord) {

				descriptCode += normalAlphabet[findIndex(binAlphabet, character)];
			}
			
			descriptCode += " ";
		}
		
		System.out.println(descriptCode);
	}
	
